import { StatusBar } from 'expo-status-bar';
import {
    StyleSheet,
    Text,
    View,
    TouchableOpacity,
    Image,
    TextInput,
} from 'react-native';
import { useState, useEffect } from 'react';
import Home from './Home';

export default function App() {
    const [name, setname] = useState('usman sadiq');
    const [Email, setmail] = useState('mianusman2488@gmail.com');
    const [arr, setarr] = useState(['car', 'bus']);
    const [count, setcount] = useState(0);
    const [message, setMessage] = useState('Hi there, how are you?');

    useEffect(() => {
        // console.log('trigger use effect hook');

        setTimeout(() => {
            setMessage("I'm fine, thanks for asking.");
        }, 1000);
    }, [message]);
    // console.log(message);
    const handlechange = (e) => {
        setmail(e);
    };
    return (
        <View style={styles.container}>
            <View style={styles.main1}>
                <Text
                    style={{
                        color: 'white',
                        marginBottom: 20,
                        fontWeight: 'bold',
                    }}
                >
                    Helo paistan zindabad
                </Text>
            </View>
            <View style={styles.main2}>
                <Image
                    style={{ width: 200, height: 100, resizeMode: 'contain' }}
                    source={require('../my-app/assets/maxresdefault.jpg')}
                    // source={{
                    //     uri: 'https://static.theprint.in/wp-content/uploads/2022/06/pakistanflag2022052912401920220601054046.jpg',
                    // }}
                />
            </View>
            <View style={styles.main3}>
                <TextInput
                    clearTextOnFocus='true'
                    // secureTextEntry='true'
                    clearButtonMode='always'
                    keyboardType={'email-address'}
                    placeholder='Enter value'
                    onChangeText={(e) => handlechange(e)}
                    style={{
                        height: 40,
                        width: '50%',
                        paddingHorizontal: 10,
                        borderWidth: 1,
                        marginTop: 20,
                        borderColor: 'yellow',
                        backgroundColor: 'white',
                    }}
                />
                <Home Email={Email} Name={name} />
                <Text>{Email}</Text>
                <TouchableOpacity
                    // onPress={() => console.log('han bhao chl rha ahs')}
                    style={{
                        backgroundColor: 'white',
                        padding: 10,
                        marginTop: 10,
                    }}
                >
                    <Text style={{ fontSize: 20, color: 'blue' }}>
                        updated array
                    </Text>
                </TouchableOpacity>
            </View>

            <StatusBar translucent barStyle='light-content' />
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 15,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: 'red',
    },
    main1: {
        flex: 1,
        padding: 15,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: 'blue',
    },
    main2: {
        flex: 1,
        padding: 15,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: 'blue',
    },
    main3: {
        flex: 1,
        padding: 15,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: 'blue',
    },
});
