import {
    StyleSheet,
    Text,
    View,
    TouchableOpacity,
    Image,
    TextInput,
    ScrollView,
    FlatList,
    Dimensions,
    ImageBackground,
    ActivityIndicator,
} from 'react-native';
function Home() {
    useEffect(() => {
        setTimeout(() => {
            axios
                .get('https://api.github.com/users')
                .then((response) => {
                    if (response.data) {
                        console.log(response.data);
                        setUsers(response.data);
                        // console.log(response.data);
                    }
                })
                .catch((error) => {
                    Alert.alert('❌', 'connot find data ');
                })
                .finally(() => setLoading(false));
        }, 1000);
    }, []);
    // console.log(message);
    const handlechange = (e) => {
        setmail(e);
    };
    const renderItem = ({ item }) => (
        <TouchableOpacity
            style={styles.itemContainer}
            onPress={() => {
                // navigation.navigate('Profile', { userDetails: item });
            }}
        >
            {/* <Image size={60} source={{ uri: item.avatar_url }} /> */}
            <Image
                source={{ uri: item.avatar_url }}
                style={{
                    height: 100,
                    width: 100,
                    borderRadius: 50,
                    resizeMode: 'contain',
                }}
            />

            <Text
                style={{
                    fontWeight: 'bold',
                    textTransform: 'uppercase',
                    color: 'blue',
                }}
            >
                {item.login}
            </Text>
        </TouchableOpacity>
    );
    return (
        <View style={styles.container}>
            <Text style={{ fontSize: 32 }}>This is Home page</Text>;
        </View>
    );
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
});

export { Home };
