import { StatusBar } from 'expo-status-bar';
import {
    StyleSheet,
    Text,
    View,
    TouchableOpacity,
    Image,
    TextInput,
    ScrollView,
    FlatList,
    Dimensions,
    ImageBackground,
} from 'react-native';
import axios from 'axios';
import { useState, useEffect } from 'react';
import Home from './Home';

export default function App() {
    const [name, setname] = useState('usman sadiq');
    const [Email, setmail] = useState('mianusman2488@gmail.com');
    const [arr, setarr] = useState(['car', 'bus']);
    const [users, setUsers] = useState(0);
    const [message, setMessage] = useState('Hi there, how are you?');

    useEffect(() => {
        axios
            .get('https://api.github.com/users')
            .then((response) => {
                if (response.data) {
                    console.log(response.data);
                    setUsers(response.data);
                    // console.log(response.data);
                }
            })
            .catch((error) => {
                Alert.alert('❌', 'connot find data ');
            });
    }, []);
    // console.log(message);
    const handlechange = (e) => {
        setmail(e);
    };
    const renderItem = ({ item }) => (
        <TouchableOpacity
            style={styles.itemContainer}
            onPress={() => {
                navigation.navigate('Profile', { userDetails: item });
            }}
        >
            {/* <Image size={60} source={{ uri: item.avatar_url }} /> */}
            <Image
                source={{ uri: item.avatar_url }}
                style={{
                    height: 100,
                    width: 100,
                    borderRadius: 50,
                    resizeMode: 'contain',
                }}
            />

            <Text
                style={{
                    fontWeight: 'bold',
                    textTransform: 'uppercase',
                    color: 'blue',
                }}
            >
                {item.login}
            </Text>
        </TouchableOpacity>
    );

    return (
        <View style={styles.container}>
            <ImageBackground
                style={{ width: '100%', height: '100%', flex: 1 }}
                resizeMode='cover'
                source={{}}
            >
                <View style={styles.main1}>
                    <Text
                        style={{
                            color: 'blue',
                            marginBottom: 20,
                            fontWeight: 'bold',
                        }}
                    >
                        Helo paistan zindabad
                    </Text>
                </View>
                <View style={styles.main2}>
                    {/* <FlatList
                        // isMounted={false}
                        // keyExtractor={keyExtractor}
                        data={users}
                        // keyExtractor={(users) => users.id}
                        renderItem={renderItem}
                    /> */}
                </View>
                <View style={styles.main3}>
                    <TouchableOpacity
                        // onPress={() => console.log('han bhao chl rha ahs')}
                        style={{
                            backgroundColor: 'white',
                            padding: 10,
                            marginTop: 10,
                        }}
                    >
                        <Text style={{ fontSize: 20, color: 'blue' }}>
                            updated array
                        </Text>
                    </TouchableOpacity>
                </View>

                <StatusBar translucent barStyle='light-content' />
            </ImageBackground>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 15,
        // backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        // backgroundColor: 'blue',
    },
    main1: {
        flex: 1,
        width: '100%',
        padding: 15,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
    main2: {
        width: '100%',
        flex: 8,
        padding: 15,
        backgroundColor: 'red',
        alignItems: 'center',
        justifyContent: 'center',
    },
    card: {
        backgroundColor: 'white',
        width: '75%',
        height: 200,
        // padding: 10,
        marginVertical: 3,
        alignItems: 'center',
        justifyContent: 'center',
        shadowColor: '#000',
        shadowOffset: {
            width: 0,
            height: 9,
        },
        shadowOpacity: 0.5,
        shadowRadius: 12.35,

        elevation: 19,
    },
    main3: {
        width: '100%',
        flex: 1,
        padding: 15,
        backgroundColor: 'yellow',
        alignItems: 'center',
        justifyContent: 'center',
    },
    itemContainer: {
        flexDirection: 'row',
        padding: 15,
        alignItems: 'center',
        justifyContent: 'space-between',
        borderWidth: 1,
        borderRadius: 10,
        borderColor: 'gray',
        margin: 15,

        // justifyContent:'center',
        // alignItems:'center',
        // padding:10,
        // borderWidth:1
    },
});
