import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, TouchableOpacity, Image } from 'react-native';
import { useState, useEffect } from 'react';
import Home from './Home';

export default function App() {
    const [name, setname] = useState('usman sadiq');
    const [Email, setmail] = useState('mianusman2488@gmail.com');
    const [arr, setarr] = useState(['car', 'bus']);
    const [count, setcount] = useState(0);
    const [message, setMessage] = useState('Hi there, how are you?');

    useEffect(() => {
        console.log('trigger use effect hook');

        setTimeout(() => {
            setMessage("I'm fine, thanks for asking.");
        }, 1000);
    }, [message]);
    console.log(message);
    return (
        <View style={styles.container}>
            <Text style={{ color: 'white', fontWeight: 'bold' }}>
                Hello world
                <Image
                    style={{ width: 220, height: 100 }}
                    // source={require('')}
                    source={{
                        uri: 'https://static.theprint.in/wp-content/uploads/2022/06/pakistanflag2022052912401920220601054046.jpg',
                    }}
                />
                <Text>
                    {'\n'}
                    Message : {message}
                </Text>
                <Text>
                    {'\n'}
                    Name : {name}
                </Text>
                <Text>
                    {'\n'}
                    Email: {Email}
                </Text>
            </Text>

            <View>
                <Home Email={Email} Name={name} />
                <TouchableOpacity
                    // onPress={() => console.log('han bhao chl rha ahs')}
                    style={{
                        backgroundColor: 'white',
                        padding: 10,
                        marginTop: 10,
                    }}
                >
                    <Text style={{ fontSize: 20, color: 'blue' }}>
                        updated array
                    </Text>
                </TouchableOpacity>
            </View>
            <StatusBar translucent barStyle='light-content' />
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 15,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: 'blue',
    },
});
