import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Button, TouchableOpacity } from 'react-native';
import { useState, useEffect } from 'react';
import Home from './Home';

export default function App() {
    const [name, setname] = useState('usman sadiq');
    const [Email, setmail] = useState('mianusman2488@gmail.com');
    const [arr, setarr] = useState(['car', 'bus']);

    useEffect(() => {
        console.log('render ir');
    });
    return (
        <View style={styles.container}>
            <Text style={{ color: 'white', fontWeight: 'bold' }}>
                Hello world pakisatn
                {/* <br /> */}
                <Text>
                    {'\n'}
                    Name : {name}
                </Text>
                <Text>
                    {'\n'}
                    Email: {Email}
                </Text>
            </Text>
            <TouchableOpacity
                style={{ backgroundColor: 'white', padding: 10, marginTop: 10 }}
            >
                <Text style={{ fontSize: 20, color: 'blue' }}>update</Text>
            </TouchableOpacity>

            <View>
                <Text style={{ color: 'white' }}>Array data hooks</Text>
                {arr.map((v, i) => (
                    // <TouchableOpacity key={i}>
                    <Text
                        style={{
                            color: 'white',
                            fontWeight: 'bold',
                            textTransform: 'uppercase',
                            display: 'flex',
                            flexDirection: 'row',
                        }}
                        key={i}
                    >
                        {v}
                    </Text>
                ))}
                <Home Email={Email} Name={name} />
                <TouchableOpacity
                    style={{
                        backgroundColor: 'white',
                        padding: 10,
                        marginTop: 10,
                    }}
                >
                    <Text style={{ fontSize: 20, color: 'blue' }}>
                        updated array
                    </Text>
                </TouchableOpacity>
            </View>
            <StatusBar
                translucent
                barStyle='light-content'
                backgroundColor='rgba(22,7,92,1)'
            />
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: 'blue',
    },
});
