import { StatusBar } from 'expo-status-bar';
import {
    StyleSheet,
    Text,
    View,
    TouchableOpacity,
    Image,
    TextInput,
} from 'react-native';
import { useState, useEffect } from 'react';
import Home from './Home';

export default function App() {
    const [name, setname] = useState('usman sadiq');
    const [Email, setmail] = useState('mianusman2488@gmail.com');
    const [arr, setarr] = useState(['car', 'bus']);
    const [count, setcount] = useState(0);
    const [message, setMessage] = useState('Hi there, how are you?');

    useEffect(() => {
        // console.log('trigger use effect hook');

        setTimeout(() => {
            setMessage("I'm fine, thanks for asking.");
        }, 1000);
    }, [message]);
    // console.log(message);
    const handlechange = (e) => {
        setmail(e);
    };
    return (
        <View style={styles.container}>
            <View style={styles.main1}>
                <Text
                    style={{
                        color: 'white',
                        marginBottom: 20,
                        fontWeight: 'bold',
                    }}
                >
                    Helo paistan zindabad
                </Text>
            </View>
            <View style={styles.main2}>
                <View style={styles.boxes}>
                    <View style={styles.items}>
                        <Image
                            style={{
                                width: '100%',
                                height: 50,
                                resizeMode: 'contain',
                            }}
                            source={{
                                uri: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSsVcPt8fbnedgdtlhPkzBJ5CpTFZnrqjCkHnVgi67HoBVYoVIBbdHW5Mno7Ad0YC23n3Q&usqp=CAU',
                            }}
                        />
                    </View>
                    <View style={styles.items}>
                        <Image
                            style={{
                                width: '100%',
                                height: 50,
                                resizeMode: 'contain',
                            }}
                            source={{
                                uri: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ_2GGvLEQ46W63qldXMX7YwBHT-Ns547wU1m5qRHhRwmtCCYve-1En9Cux4igMjk6Dq4c&usqp=CAU',
                            }}
                        />
                    </View>
                    <View style={styles.items}>
                        <Image
                            style={{
                                width: '100%',
                                height: 50,
                                resizeMode: 'contain',
                            }}
                            source={{
                                uri: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSsVcPt8fbnedgdtlhPkzBJ5CpTFZnrqjCkHnVgi67HoBVYoVIBbdHW5Mno7Ad0YC23n3Q&usqp=CAU',
                            }}
                        />
                    </View>

                    <View style={styles.items}>
                        <Image
                            style={{
                                width: '100%',
                                height: 50,
                                resizeMode: 'contain',
                            }}
                            source={{
                                uri: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSsVcPt8fbnedgdtlhPkzBJ5CpTFZnrqjCkHnVgi67HoBVYoVIBbdHW5Mno7Ad0YC23n3Q&usqp=CAU',
                            }}
                        />
                    </View>
                    <View style={styles.items}>
                        <Image
                            style={{
                                width: '100%',
                                height: 50,
                                resizeMode: 'contain',
                            }}
                            source={{
                                uri: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSsVcPt8fbnedgdtlhPkzBJ5CpTFZnrqjCkHnVgi67HoBVYoVIBbdHW5Mno7Ad0YC23n3Q&usqp=CAU',
                            }}
                        />
                    </View>
                    <View style={styles.items}>
                        <Image
                            style={{
                                width: '100%',
                                height: 50,
                                resizeMode: 'contain',
                            }}
                            source={{
                                uri: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSsVcPt8fbnedgdtlhPkzBJ5CpTFZnrqjCkHnVgi67HoBVYoVIBbdHW5Mno7Ad0YC23n3Q&usqp=CAU',
                            }}
                        />
                    </View>
                </View>

                <Home Email={Email} Name={name} />
                <Text>{Email}</Text>
                <TextInput
                    clearTextOnFocus='true'
                    // secureTextEntry='true'
                    clearButtonMode='always'
                    keyboardType={'email-address'}
                    placeholder='Enter value'
                    onChangeText={(e) => handlechange(e)}
                    style={{
                        height: 40,
                        width: '50%',
                        paddingHorizontal: 10,
                        borderWidth: 1,
                        marginTop: 20,
                        borderColor: 'yellow',
                        backgroundColor: 'white',
                    }}
                />
            </View>
            <View style={styles.main3}>
                <TouchableOpacity
                    // onPress={() => console.log('han bhao chl rha ahs')}
                    style={{
                        backgroundColor: 'white',
                        padding: 10,
                        marginTop: 10,
                    }}
                >
                    <Text style={{ fontSize: 20, color: 'blue' }}>
                        updated array
                    </Text>
                </TouchableOpacity>
            </View>

            <StatusBar translucent barStyle='light-content' />
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 15,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: 'blue',
    },
    main1: {
        flex: 1,
        width: '100%',
        padding: 15,
        backgroundColor: 'red',
        alignItems: 'center',
        justifyContent: 'center',
    },
    main2: {
        flex: 8,
        width: '100%',
        // padding: 15,
        backgroundColor: 'white',
    },
    main3: {
        flex: 1,
        width: '100%',
        padding: 15,

        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: 'green',
    },
    boxes: {
        marginTop: 10,
        flexDirection: 'row',
        justifyContent: 'center',
        marginBottom: 30,
        flexWrap: 'wrap',
    },
    items: {
        width: '33%',
        // height: 200,
    },
});
