import { StatusBar } from 'expo-status-bar';
import {
    StyleSheet,
    Text,
    View,
    TouchableOpacity,
    Image,
    TextInput,
} from 'react-native';
import { useState, useEffect } from 'react';
import Home from './Home';

export default function App() {
    const [name, setname] = useState('usman sadiq');
    const [Email, setmail] = useState('mianusman2488@gmail.com');
    const [arr, setarr] = useState(['car', 'bus']);
    const [count, setcount] = useState(0);
    const [message, setMessage] = useState('Hi there, how are you?');

    useEffect(() => {
        // console.log('trigger use effect hook');

        setTimeout(() => {
            setMessage("I'm fine, thanks for asking.");
        }, 1000);
    }, [message]);
    // console.log(message);
    const handlechange = (e) => {
        setmail(e);
    };
    return (
        <View style={styles.container}>
            <View style={styles.main1}>
                <Text
                    style={{
                        color: 'blue',
                        marginBottom: 20,
                        fontWeight: 'bold',
                    }}
                >
                    Helo paistan zindabad
                </Text>
            </View>
            <View style={styles.main2}>
                <View style={styles.card}>
                    <Text>Usman sadiq</Text>
                </View>
            </View>
            <View style={styles.main3}>
                <TouchableOpacity
                    // onPress={() => console.log('han bhao chl rha ahs')}
                    style={{
                        backgroundColor: 'white',
                        padding: 10,
                        marginTop: 10,
                    }}
                >
                    <Text style={{ fontSize: 20, color: 'blue' }}>
                        updated array
                    </Text>
                </TouchableOpacity>
            </View>

            <StatusBar translucent barStyle='light-content' />
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 15,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: 'blue',
    },
    main1: {
        flex: 1,
        width: '100%',
        padding: 15,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
    main2: {
        width: '100%',
        flex: 7,
        padding: 15,
        backgroundColor: 'red',
        alignItems: 'center',
        justifyContent: 'center',
    },
    card: {
        backgroundColor: 'white',
        width: '75%',
        height: 200,
    },
    main3: {
        width: '100%',
        flex: 1,
        padding: 15,
        backgroundColor: 'yellow',
        alignItems: 'center',
        justifyContent: 'center',
    },
});
